import { Router } from "express";
import { verifyAuthMiddleware } from "../middlewares/verifyAuth.middlewares.js";
import { commentCharacterController, commentInteractionController, createCharacterController, deleteCommentController, editCommentController, getAllCharactersController, getCharacterByIdController, getCharacterByUserIdController, getCommentsByCharacterIdController, operationCharacterController, uploadCharacterAvatarController } from "../controllers/character.controller.js";
import { userMetaDataMiddleware } from "../middlewares/userMetaData.middleware.js";
import { uploadSingleImage } from "../middlewares/upload.middlewares.js";
const charactersRouter = Router();
charactersRouter.post('/create', verifyAuthMiddleware, userMetaDataMiddleware, createCharacterController);
charactersRouter.get("/get/:id", getCharacterByIdController);
charactersRouter.get("/getAll", getAllCharactersController);
charactersRouter.post("/operation", verifyAuthMiddleware, operationCharacterController);
charactersRouter.post("/comment", verifyAuthMiddleware, userMetaDataMiddleware, commentCharacterController);
charactersRouter.delete("/comment", verifyAuthMiddleware, userMetaDataMiddleware, deleteCommentController);
charactersRouter.put("/comment", verifyAuthMiddleware, userMetaDataMiddleware, editCommentController);
charactersRouter.get("/getByUserId", verifyAuthMiddleware, userMetaDataMiddleware, getCharacterByUserIdController);
charactersRouter.get("/comment/:id", verifyAuthMiddleware, getCommentsByCharacterIdController);
charactersRouter.post("/comment/operation", verifyAuthMiddleware, commentInteractionController);
charactersRouter.post("/uploadAvatar", verifyAuthMiddleware, uploadSingleImage, uploadCharacterAvatarController);
export default charactersRouter;
